import { useEffect } from "react";
import { useState } from "react";
// import { Link } from "react-router-dom";
import './Movies.css'

const MovieList = ()=>{
    const [movie,setmovie]=useState([])
    const [loading,setloading]=useState(false)

    useEffect(()=>{
        setloading(true)
        const data = axios.get('https://www.omdbapi.com/?i=tt3896198&apikey=8a7484af')
        data.then((res)=>{
            console.log(res)
            setmovie(res.data)
            setloading(false)
        }).catch((err)=>{
            alert(err.message)
        })  
        
