import axios from "axios";
import { useEffect } from "react";
import { useState } from "react";
import { Link } from "react-router-dom";
import './Movies.css'

const MovieList = ()=>{
    const [movie,setmovie]=useState([])
    const [loading,setloading]=useState(false)

    // useEffect(()=>{
    //     setloading(true)
    //     const data = axios.get('https://www.omdbapi.com/?apikey=8a7484af&s=*&type=movie')
    //     data.then((res)=>{
    //         console.log(res.data)
    //         setmovie(res.data)
    //         setloading(false)
    //     }).catch((err)=>{
    //         alert(err.message)
    //     })  
    //   },[])


    useEffect(() => {
      axios.get('https://www.omdbapi.com/?apikey=8a7484af&s=*&type=movie&y=2020')
        .then(response => {
          console.log("console.",response)
          // setMovies(response.data.Search);
          // setLoading(false);
        })
        .catch(error => {
          console.log(error);
          // setLoading(false);
        });
    }, []);

    return <div>
         <div className="col">
        {loading && (
          <span style={{ textAlign: "center" }}>Loading a content....</span>
        )}
        {!loading &&
            <div>
              {
                <div className="col1">
                  <img
                    src={
                      movie.Poster == "N/A"
                        ? "https://cdn.pixabay.com/photo/2017/09/04/09/37/cinema-strip-2713352_960_720.jpg"
                        : movie.Poster
                    }
                    className="si"
                  />
                  <div>
                    <h4>{movie.Title}</h4>
                    <span>{movie.Year}</span>
                  </div>
                  <li><Link  to='/movies/p2'>Know More</Link></li>
                </div>
              }
            </div>
          }
      </div>
    </div>
}

export default MovieList;



