import { useParams } from 'react-router-dom';
import { useState,useEffect } from 'react';

function MovieListDetail() {
  const { id } = useParams();
  // make API call using id to fetch movie details
  const [movie, setMovie] = useState({});
// useEffect(() => {
//   fetch(`http://www.omdbapi.com/?apikey=8a7484af&i=${id}`)
//     .then(response => response.json())
//     .then((data) => { 
//       console.log(data)
//       setMovie(data)})
// }, [id])

const fetchMovies = async () => {

  try {
    const response = await axios.get(
      `http://www.omdbapi.com/?apikey=8a7484af&i=${id}`
    );
    // setMovies((prevMovies) => [...prevMovies, ...response.data.Search]);
     console.log(response)

  } catch (err) {
    alert(err.message);
  }
  
};

useEffect(() => {
  fetchMovies();
}, [id]);

   return(
    <div>
         <div className="detail">
          <h4>{movie.Title}</h4>
          <img src={movie.Poster} alt="Movie Poster" />
          <p>Year: {movie.Year}</p>
          <p>Plot: {movie.Plot}</p>
          <p>Actors: {movie.Actors}</p>
          <p>Director: {movie.Director}</p>
          <p>IMDB Rating: {movie.imdbRating}</p>
        </div>
    </div>
   )
}
export default MovieListDetail
